import { useEffect, useState } from "react";

function App() {
  // ---------- STATE ----------
  const [name, setName] = useState("");
  const [playlists, setPlaylists] = useState([]);
  const [screens, setScreens] = useState([]);

  const [selectedScreen, setSelectedScreen] = useState("");
  const [selectedPlaylist, setSelectedPlaylist] = useState("");

  // ---------- CREATE PLAYLIST ----------
  async function createPlaylist() {
    if (!name) {
      alert("Enter playlist name");
      return;
    }

    await fetch("/playlist", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name })
    });

    setName("");
    loadData();
  }

  // ---------- LOAD PLAYLISTS & SCREENS ----------
  async function loadData() {
    const playlistsRes = await fetch("/playlists");
    const screensRes = await fetch("/screens");

    setPlaylists(await playlistsRes.json());
    setScreens(await screensRes.json());
  }

  // ---------- ASSIGN PLAYLIST ----------
  async function assignPlaylist() {
    if (!selectedScreen || !selectedPlaylist) {
      alert("Select screen and playlist");
      return;
    }

    await fetch("/screen/assign", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        screenId: selectedScreen,
        playlistId: selectedPlaylist
      })
    });

    alert("Playlist assigned");
  }

  // ---------- INITIAL LOAD ----------
  useEffect(() => {
    loadData();
  }, []);

  // ---------- UI ----------
  return (
    <div style={{ padding: 40 }}>
      <h2>Digital Signage Admin</h2>

      {/* Create Playlist */}
      <h3>Create Playlist</h3>
      <input
        value={name}
        onChange={e => setName(e.target.value)}
        placeholder="Playlist name"
      />
      <button onClick={createPlaylist}>Create</button>

      {/* List Playlists */}
      <h3>Playlists</h3>
      <ul>
        {playlists.map(p => (
          <li key={p.id}>{p.name}</li>
        ))}
      </ul>

      {/* Assign Playlist */}
      <h3>Assign Playlist to Screen</h3>

      <select onChange={e => setSelectedScreen(e.target.value)}>
        <option value="">Select Screen</option>
        {screens.map(s => (
          <option key={s.id} value={s.id}>
            {s.name}
          </option>
        ))}
      </select>

      <select onChange={e => setSelectedPlaylist(e.target.value)}>
        <option value="">Select Playlist</option>
        {playlists.map(p => (
          <option key={p.id} value={p.id}>
            {p.name}
          </option>
        ))}
      </select>

      <button onClick={assignPlaylist}>Assign</button>
    </div>
  );
}

export default App;
