import { useEffect, useState } from "react";
import api from "../../api/services";
import ApplyGroupLayoutButton from "./ApplyGroupLayoutButton";

export default function ScreenGroupManager({
  screenId,
  onApplyLayout
}) {
  const [groups, setGroups] = useState([]);
  const [selectedGroupId, setSelectedGroupId] = useState("");

  useEffect(() => {
    loadGroups();
  }, []);

  async function loadGroups() {
    try {
      const res = await api.get("/screen-groups");
      setGroups(res.data || []);
      setSelectedGroupId(""); // âœ… reset safely
    } catch (err) {
      console.error(err);
    }
  }

  const selectedGroup = groups.find(
    g => g._id === selectedGroupId
  );

  // ðŸ”‘ SUPPORT BOTH BACKEND SHAPES
  const layout =
    selectedGroup?.layout || selectedGroup?.zones || [];

  return (
    <div className="bg-gray-800 p-3 rounded-xl text-sm">
      <div className="font-semibold mb-2">ðŸ–¥ Screen Groups</div>

      <select
        value={selectedGroupId}
        onChange={e => setSelectedGroupId(e.target.value)}
        className="w-full bg-black border border-gray-700 rounded p-1 mb-2"
      >
        <option value="">Select group</option>
        {groups.map(g => (
          <option key={g._id} value={g._id}>
            {g.name}
          </option>
        ))}
      </select>

      {selectedGroup && layout.length > 0 && (
        <ApplyGroupLayoutButton
          screenId={screenId}
          layout={layout}
          onApply={onApplyLayout}
        />
      )}

      {selectedGroup && !layout.length && (
        <div className="text-xs text-gray-400">
          This group has no layout
        </div>
      )}
    </div>
  );
}
