import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/services";
import socket from "../utils/socket";
import usePlaylistPlayer from "../hooks/usePlaylistPlayer";
import ScreenControls from "../components/ScreenControls";

export default function ScreenPreview() {
  const { screenId } = useParams();
  const [screen, setScreen] = useState(null);
  const [mode, setMode] = useState("horizontal");
  const [items, setItems] = useState([]);
  const [playlistId, setPlaylistId] = useState(null);

  /* üîÅ auto-rotate logic */
  const { current, remaining, skip, setPaused } =
    usePlaylistPlayer(items);
  const currentItem = items?.[current?.index] ?? null;

  /* LOAD SCREEN ‚Üí GET PLAYLIST */
  useEffect(() => {
    async function loadScreen() {
      const res = await api.get(`/screens/${screenId}`);
      setScreen(res.data);
      setPlaylistId(res.data.playlistId);
    }
    loadScreen();
  }, [screenId]);

  /* LOAD PLAYLIST ITEMS + SOCKET */
  useEffect(() => {
    if (!playlistId) return;

    loadPlaylist();

    socket.emit("register-playlist", playlistId);

    socket.on("playlist-updated", ({ playlistId: id }) => {
      if (id === playlistId) loadPlaylist();
    });

    return () => {
      socket.off("playlist-updated");
    };
  }, [playlistId]);

  async function loadPlaylist() {
    try {
      const res = await api.get(`/playlists/${playlistId}/items`);
      setItems(res.data || []);
    } catch (err) {
      console.warn("‚ö† Using cached playlist");
      const cached = localStorage.getItem(`playlist-cache-${screenId}`);
      if (cached) setItems(JSON.parse(cached));
    }
  }

  /* üíæ CACHE PLAYLIST (offline fallback) */
  useEffect(() => {
    if (items.length) {
      localStorage.setItem(`playlist-cache-${screenId}`, JSON.stringify(items));
    }
  }, [items, screenId]);

  useEffect(() => {
    if (!currentItem?.mediaId?._id) return;

    api
      .post("/analytics/log", {
        screenId,
        mediaId: currentItem.mediaId._id,
      })
      .catch((err) => {
        console.warn("Analytics log failed", err.message);
      });
  }, [currentItem, screenId]);

  useEffect(() => {
    if (!screenId) return;

    socket.emit("register-screen", screenId);

    return () => {
      socket.off("player-control");
    };
  }, [screenId]);

  useEffect(() => {
  socket.emit("register-screen", screenId);

  socket.on("player-control", (action) => {
    if (action === "pause") videoRef?.current?.pause();
    if (action === "play") videoRef?.current?.play();
    if (action === "skip") skipToNext();
  });

  return () => socket.off("player-control");
}, [screenId]);

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      {/* HEADER */}
      <div className="flex justify-between items-center mb-4">
        {screen && (
          <div className="flex justify-center">
            <div className="bg-gray-800 rounded-xl p-4 flex items-center gap-4 shadow-lg">
              <div>
                <div className="font-semibold">{screen.name}</div>
                <div className="text-xs text-gray-400">
                  {screen.location || "No location"}
                </div>
              </div>

              <ScreenControls screenId={screen._id} />
            </div>
          </div>
        )}

        {/* MODE TABS */}
        <div className="flex bg-gray-800 rounded-lg overflow-hidden">
          <button
            onClick={() => setMode("horizontal")}
            className={`px-4 py-2 text-sm ${
              mode === "horizontal" ? "bg-indigo-600" : "hover:bg-gray-700"
            }`}
          >
            Horizontal
          </button>
          <button
            onClick={() => setMode("vertical")}
            className={`px-4 py-2 text-sm ${
              mode === "vertical" ? "bg-indigo-600" : "hover:bg-gray-700"
            }`}
          >
            Vertical
          </button>
        </div>
      </div>

      {/* PREVIEW AREA */}
      <div className="flex justify-center mt-6">
        <div
          className={`relative bg-black rounded-xl shadow-lg overflow-hidden ${
            mode === "horizontal"
              ? "w-[900px] h-[500px]"
              : "w-[400px] h-[700px]"
          }`}
        >
          {/* MEDIA */}
          {current?.mediaId?.type === "image" && (
            <img
              src={current.mediaId.url}
              className="w-full h-full object-cover"
              alt=""
            />
          )}

          {current?.mediaId?.type === "video" && (
            <video
              src={current.mediaId.url}
              autoPlay
              muted
              className="w-full h-full object-cover"
            />
          )}

          {/* COUNTDOWN */}
          {current && (
            <div className="absolute top-3 right-3 bg-black/70 text-white text-xs px-3 py-1 rounded-full">
              ‚è± {remaining}s
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
