import { useEffect, useState } from "react";
import api from "../api/services";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";

export default function PlaylistBuilder({ playlistId }) {
  const [media, setMedia] = useState([]);
  const [items, setItems] = useState([]);
  const [mediaId, setMediaId] = useState("");
  const [duration, setDuration] = useState(10);
  const [file, setFile] = useState(null);

  /* LOAD MEDIA LIBRARY */
  useEffect(() => {
    api.get("/media").then((res) => setMedia(res.data));
  }, []);

  /* LOAD PLAYLIST ITEMS */
  useEffect(() => {
    if (!playlistId) return;
    loadItems();
  }, [playlistId]);

  async function loadItems() {
    const res = await api.get(`/playlists/${playlistId}/items`);
    setItems(res.data);
  }

  /* UPLOAD MEDIA */
async function uploadMedia() {
  if (!file) return alert("Select a file");

  try {
    const form = new FormData();
    form.append("file", file); // ðŸ”´ MUST be "file"

    await api.post("/media/upload", form);

    setFile(null);

    const res = await api.get("/media");
    setMedia(res.data);
  } catch (err) {
    console.error("UPLOAD ERROR:", err.response?.data || err.message);
    alert(err.response?.data?.error || "Upload failed");
  }
}

  /* ADD TO PLAYLIST */
  async function addToPlaylist() {
    if (!mediaId) return alert("Select media");

    await api.post("/playlists/items", {
      playlistId,
      mediaId,
      duration: Number(duration),
      position: Date.now(),
    });

    setMediaId("");
    loadItems();
  }

  function onDragEnd(result) {
  console.log("DRAG RESULT:", result);
  if (!result.destination) return;

  // const reordered = Array.from(items);
  // const [moved] = reordered.splice(result.source.index, 1);
  // reordered.splice(result.destination.index, 0, moved);

  // setItems(reordered);

  // api.post("/playlist/reorder", {
  //   playlistId,
  //   items: reordered.map((i, index) => ({
  //     id: i._id,
  //     position: index
  //   }))
  // });
}

  return (
    <div className="space-y-3 bg-gray-50 p-3 rounded overflow-visible">
      {/* Upload */}
      <div className="flex gap-2">
        <input type="file"  accept="image/*,video/*" onChange={(e) => setFile(e.target.files[0])} />
        <button onClick={uploadMedia} className="text-sm text-blue-600">
          Upload
        </button>
      </div>

      {/* Add media */}
      <div className="flex gap-2">
        <select
          className="border p-2 rounded"
          value={mediaId}
          onChange={(e) => setMediaId(e.target.value)}
        >
          <option value="">Select Media</option>
          {media.map((m) => (
            <option key={m._id} value={m._id}>
              {m.filename} ({m.type})
            </option>
          ))}
        </select>

        <input
          type="number"
          className="border p-2 w-20"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
        />

        <button
          onClick={addToPlaylist}
          className="bg-indigo-600 text-white px-3 rounded"
        >
          Add
        </button>
      </div>

    {/* PLAYLIST ITEMS */}
    <DragDropContext onDragEnd={onDragEnd}>
  <Droppable droppableId="playlist">
    {(provided) => (
      <div
        ref={provided.innerRef}
        {...provided.droppableProps}
        className="space-y-2"
      >
        {items.map((item, index) => (
          <Draggable
            key={item._id}
            draggableId={item._id}
            index={index}
          >
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.draggableProps}
                {...provided.dragHandleProps}
                className="flex items-center gap-3 p-2 bg-white rounded shadow cursor-grab active:cursor-grabbing"
              >
                <span className="text-gray-400">â˜°</span>

                {item.mediaId?.type === "image" ? (
                  <img
                    src={item.mediaId.url}
                    className="w-16 h-10 rounded object-cover"
                  />
                ) : (
                  <video
                    src={item.mediaId?.url}
                    className="w-16 h-10 rounded"
                    muted
                  />
                )}

                <span className="flex-1 truncate">
                  {item.mediaId?.filename}
                </span>

                <span className="text-xs text-gray-500">
                  {item.duration}s
                </span>
              </div>
            )}
          </Draggable>
        ))}

        {provided.placeholder}
      </div>
    )}
  </Droppable>
</DragDropContext>


    </div>
  );
}
