import { useEffect, useState } from "react";
// import Player from "./components/Player";
import api from "../src/api/services";
import socket from "../utils/socket";


function App() {
  // ---------- STATE ----------
  const [name, setName] = useState("");
  const [playlists, setPlaylists] = useState([]);
  const [screens, setScreens] = useState([]);

  const [selectedScreen, setSelectedScreen] = useState("");
  const [selectedPlaylist, setSelectedPlaylist] = useState("");
  const [media, setMedia] = useState([]);
  const [selectedMedia, setSelectedMedia] = useState("");
  const [duration, setDuration] = useState(10);
  const [screenName, setScreenName] = useState("");
  const [location, setLocation] = useState("");

  // ---------- LOAD DATA ----------
  async function loadData() {
    const [playlists, screens, media] = await Promise.all([
    api.get("/playlists"),
    api.get("/screens"),
    api.get("/media")
  ]);

    setPlaylists(playlists.data);
    setScreens(screens.data);
    setMedia(media.data);
  }

  // ---------- CREATE PLAYLIST ----------
  async function createPlaylist() {
    if (!name) {
      alert("Enter playlist name");
      return;
    }

    await api.post("/playlists", { name });

    setName("");
    loadData();
  }

  // ---------- ASSIGN PLAYLIST ----------
  async function assignPlaylist() {
  if (!selectedScreen || !selectedPlaylist)
    return alert("Select screen and playlist");

  await api.post("/assign", {
    screenId: selectedScreen,
    playlistId: selectedPlaylist
  });

  alert("Playlist assigned");
}


  // ---------- INITIAL LOAD ----------
  useEffect(() => {
    loadData();
    socket.connect();

    socket.on("playlist-updated", () => {
      console.log("ðŸ”„ Playlist updated â†’ reload data");
      loadData();
    });

    return () => {
      socket.off("playlist-updated");
      socket.disconnect();
    };
  }, []);

  async function uploadMedia(file) {
  if (!file) return;

  const formData = new FormData();
  formData.append("file", file);

  await api.post("/media/upload", formData, {
    headers: { "Content-Type": "multipart/form-data" }
  });

  alert("Media uploaded");
  loadData();
}

async function addToPlaylist() {
  if (!selectedPlaylist || !selectedMedia)
    return alert("Select playlist and media");

  await api.post("/playlists/items", {
    playlistId: selectedPlaylist,
    mediaId: selectedMedia,
    duration,
    position: Date.now()
  });

  alert("Added to playlist");
}


async function createScreen() {
  if (!screenName) return alert("Enter screen name");

  await api.post("/screens", {
    name: screenName,
    location
  });

  setScreenName("");
  setLocation("");
  loadData();
}


  // ---------- UI ----------
  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow p-6 space-y-6">
        <h1 className="text-2xl font-bold">Digital Signage Admin</h1>

        {/* Assign Playlist */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <select
            className="border p-2 rounded"
            value={selectedScreen}
            onChange={(e) => setSelectedScreen(e.target.value)}
          >
            <option value="">Select Screen</option>
            {screens.map((s) => (
              <option key={s._id} value={s._id}>
                {s.name}
              </option>
            ))}
          </select>

          <select
            className="border p-2 rounded"
            value={selectedPlaylist}
            onChange={(e) => setSelectedPlaylist(e.target.value)}
          >
            <option value="">Select Playlist</option>
            {playlists.map((p) => (
              <option key={p._id} value={p._id}>
                {p.name}
              </option>
            ))}
          </select>

          <button
            onClick={assignPlaylist}
            className="bg-blue-600 hover:bg-blue-700 text-white rounded px-4 py-2"
          >
            Assign Playlist
          </button>
        </div>

        {/* Create Playlist */}
        <div className="border-t pt-4">
          <h3 className="font-semibold mb-2">Create Playlist</h3>

          <div className="flex gap-2">
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Playlist name"
              className="border p-2 rounded flex-1"
            />
            <button
              onClick={createPlaylist}
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
            >
              Create
            </button>
          </div>
        </div>
        <div className="border-t pt-4">
          <h3 className="font-semibold mb-2">Create Screen</h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-2">
            <input
              className="border p-2 rounded"
              placeholder="Screen name"
              value={screenName}
              onChange={(e) => setScreenName(e.target.value)}
            />

            <input
              className="border p-2 rounded"
              placeholder="Location (optional)"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />

            <button
              onClick={createScreen}
              className="bg-indigo-600 text-white rounded px-4 py-2"
            >
              Create Screen
            </button>
          </div>
        </div>

        {/* Upload Media */}
        <div className="border-t pt-4">
          <h3 className="font-semibold mb-2">Upload Media</h3>

          <input
            type="file"
            accept="image/*,video/*"
            onChange={(e) => uploadMedia(e.target.files[0])}
            className="mb-2"
          />
        </div>
        {/* Playlist Builder */}
        <div className="border-t pt-4">
          <h3 className="font-semibold mb-2">Playlist Timeline</h3>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-2 mb-2">
            <select
              className="border p-2 rounded"
              value={selectedPlaylist}
              onChange={(e) => setSelectedPlaylist(e.target.value)}
            >
              <option value="">Select Playlist</option>
              {playlists.map((p) => (
                <option key={p._id} value={p._id}>
                  {p.name}
                </option>
              ))}
            </select>

            <select
              className="border p-2 rounded"
              value={selectedMedia}
              onChange={(e) => setSelectedMedia(e.target.value)}
            >
              <option value="">Select Media</option>
              {media.map((m) => (
                <option key={m._id} value={m._id}>
                  {m.filename} ({m.type})
                </option>
              ))}
            </select>

            <input
              type="number"
              value={duration}
              onChange={(e) => setDuration(e.target.value)}
              className="border p-2 rounded"
              placeholder="Duration (sec)"
            />

            <button
              onClick={addToPlaylist}
              className="bg-purple-600 text-white rounded px-3"
            >
              Add
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
