export default function resolveZoneContent({
  zone,
  zonePlaylistItems,
  groupPlaylistItems,
  fallback
}) {
  if (zonePlaylistItems?.length) return zonePlaylistItems;
  if (groupPlaylistItems?.length) return groupPlaylistItems;
  if (fallback) return [{ mediaId: fallback }];
  return [];
}
