import { useEffect, useState } from "react";
import api from "../api/services";

export default function PlaylistBuilder({ playlistId }) {
  const [media, setMedia] = useState([]);
  const [items, setItems] = useState([]);
  const [mediaId, setMediaId] = useState("");
  const [duration, setDuration] = useState(10);
  const [file, setFile] = useState(null);
  const [dragIndex, setDragIndex] = useState(null);


  /* LOAD MEDIA LIBRARY */
  useEffect(() => {
    api.get("/media").then((res) => setMedia(res.data));
  }, []);

  /* LOAD PLAYLIST ITEMS */
  useEffect(() => {
  if (!playlistId) return;

  // Initial load
  loadItems();

  //Join playlist socket room
  socket.emit("register-playlist", playlistId);

  // Listen for reorder / update
  const onPlaylistUpdated = ({ playlistId: updated }) => {
    if (updated === playlistId) {
      console.log("ðŸ”„ Playlist updated â€” reloading items");
      loadItems();
    }
  };

  socket.on("playlist-updated", onPlaylistUpdated);

  // Cleanup (VERY IMPORTANT)
  return () => {
    socket.off("playlist-updated", onPlaylistUpdated);
  };
}, [playlistId]);


  async function loadItems() {
    const res = await api.get(`/playlists/${playlistId}/items`);
    setItems(res.data);
  }

  /* UPLOAD MEDIA */
  async function uploadMedia() {
    if (!file) return alert("Select a file");

    try {
      const form = new FormData();
      form.append("file", file); // ðŸ”´ MUST be "file"

      await api.post("/media/upload", form);

      setFile(null);

      const res = await api.get("/media");
      setMedia(res.data);
    } catch (err) {
      console.error("UPLOAD ERROR:", err.response?.data || err.message);
      alert(err.response?.data?.error || "Upload failed");
    }
  }

  /* ADD TO PLAYLIST */
  async function addToPlaylist() {
    if (!mediaId) return alert("Select media");

    await api.post("/playlists/items", {
      playlistId,
      mediaId,
      duration: Number(duration),
      position: Date.now(),
    });

    setMediaId("");
    loadItems();
  }

function handleDrop(dropIndex) {
  if (dragIndex === null || dragIndex === dropIndex) return;

  const reordered = [...items];
  const [moved] = reordered.splice(dragIndex, 1);
  reordered.splice(dropIndex, 0, moved);

  setItems(reordered);
  setDragIndex(null);

  api.post("/playlists/reorder", {
    playlistId,
    items: reordered.map((item, index) => ({
      id: item._id,
      position: index
    }))
  });
}

  return (
    <div className="space-y-3 bg-gray-50 p-3 rounded overflow-visible">
      {/* Upload */}
      <div className="flex gap-2">
        <input
          type="file"
          accept="image/*,video/*"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <button onClick={uploadMedia} className="text-sm text-blue-600">
          Upload
        </button>
      </div>

      {/* Add media */}
      <div className="flex gap-2">
        <select
          className="border p-2 rounded"
          value={mediaId}
          onChange={(e) => setMediaId(e.target.value)}
        >
          <option value="">Select Media</option>
          {media.map((m) => (
            <option key={m._id} value={m._id}>
              {m.filename} ({m.type})
            </option>
          ))}
        </select>

        <input
          type="number"
          className="border p-2 w-20"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
        />

        <button
          onClick={addToPlaylist}
          className="bg-indigo-600 text-white px-3 rounded"
        >
          Add
        </button>
      </div>

      {/* PLAYLIST ITEMS */}
      <div className="space-y-2">
      {items.map((item, index) => (
        <div
          key={item._id}
          draggable
          onDragStart={(e) => {
            setDragIndex(index);
            e.currentTarget.classList.add("dragging");
          }}
          onDragEnd={(e) => {
            e.currentTarget.classList.remove("dragging");
          }}
          onDragOver={(e) => e.preventDefault()}
          onDrop={() => handleDrop(index)}
          className="flex items-center gap-3 p-2 bg-white rounded shadow cursor-move hover:bg-indigo-50 transition"
        >
          {/* drag handle */}
          <span className="text-gray-400 text-lg">â˜°</span>

          {/* media preview */}
          {item.mediaId?.type === "image" ? (
            <img
              src={item.mediaId.url}
              className="w-16 h-10 object-cover rounded"
            />
          ) : (
            <video
              src={item.mediaId?.url}
              className="w-16 h-10 rounded"
              muted
            />
          )}

          {/* filename */}
          <span className="flex-1 truncate text-sm">
            {item.mediaId?.filename}
          </span>

          {/* duration */}
          <span className="text-xs text-gray-500">
            {item.duration}s
          </span>
        </div>
      ))}
    </div>
  </div>
  );
}
