import { forwardRef } from "react";

const MediaPlayer = forwardRef(({ media, loop = false }, ref) => {
  if (!media) return null;

  if (media.type === "image") {
    return (
      <img
        src={media.url}
        alt=""
        className="w-full h-full object-cover"
      />
    );
  }

  if (media.type === "video") {
    return (
      <video
        ref={ref}
        src={media.url}
        autoPlay
        muted
        loop={loop}
        className="w-full h-full object-cover"
      />
    );
  }

  return null;
});

export default MediaPlayer;
