import { PlayCircle, MapPin } from "lucide-react";
import { useNavigate } from "react-router-dom";
import ScreensTable from "./ScreenTable";

export default function PlayerCard({ screen }) {
  const navigate = useNavigate();
  const isOnline = screen.status === "online";

  return (
    <div
      className="group relative rounded-xl border bg-white p-4 shadow-sm transition-all duration-300 hover:-translate-y-1 hover:shadow-lg cursor-pointer"
    >
      <ScreensTable screens={[screen]} />
    </div>
  );
}
