import Zone from "./Zone";

function ZoneEditor({ zones, setZones, containerRef, playlists }) {

  // ✅ THIS WAS MISSING
  function handleZoneChange(updatedZone) {
    setZones(prev =>
      prev.map(z =>
        z._id === updatedZone._id ? updatedZone : z
      )
    );
  }

  function updateZone(zoneId, updates) {
    setZones(prev =>
      prev.map(z =>
        z._id === zoneId ? { ...z, ...updates } : z
      )
    );
  }

  return (
    <>
      {zones.map(zone => (
        <Zone
          key={zone._id}
          zone={zone}
          containerRef={containerRef}
          playlists={playlists}
          onChange={handleZoneChange}
          updateZone={updateZone}
        />
      ))}
    </>
  );
};

export default ZoneEditor;
