import { io } from "socket.io-client";

const socket = io("http://localhost:8080", {
  transports: ["websocket"],
  autoConnect: true,
});

export function onGroupLayoutUpdated(cb) {
  socket.on("group-layout-updated", cb);
}

export function offGroupLayoutUpdated(cb) {
  socket.off("group-layout-updated", cb);
}

export default socket;
