import { useEffect, useState } from "react";
import api from "../api/services";

export default function PlaylistBuilder({ playlistId }) {
  const [media, setMedia] = useState([]);
  const [mediaId, setMediaId] = useState("");
  const [duration, setDuration] = useState(10);

  useEffect(() => {
    api.get("/media").then(res => setMedia(res.data));
  }, []);

  async function addToPlaylist() {
  if (!selectedPlaylist || !selectedMedia) {
    alert("Select playlist and media");
    return;
  }

  await api.post("/playlist-items", {
    playlistId: selectedPlaylist,
    mediaId: selectedMedia, 
    duration: Number(duration) || 10,
    position: Date.now()
  });

  // reload playlist items
  loadItems();
}


  return (
    <div className="flex gap-2">
      <select onChange={e => setMediaId(e.target.value)} className="border p-2">
        <option value="">Select Media</option>
        {media.map(m => (
          <option key={m._id} value={m._id}>
            {m.filename}
          </option>
        ))}
      </select>

      <input
        type="number"
        value={duration}
        onChange={e => setDuration(e.target.value)}
        className="border p-2 w-24"
      />

      <button
        onClick={addItem}
        className="bg-indigo-600 text-white px-4 rounded"
      >
        Add
      </button>
    </div>
  );
}
