import { Rnd } from "react-rnd";

function Zone({
  zone,
  containerRef,
  onChange,
  playlists,
  updateZone
}) {
  if (!containerRef?.current) return null;

  const { offsetWidth, offsetHeight } = containerRef.current;

  return (
    <Rnd
      disableDragging={zone.locked}
      enableResizing={!zone.locked}
      size={{
        width: zone.w * offsetWidth,
        height: zone.h * offsetHeight
      }}
      position={{
        x: zone.x * offsetWidth,
        y: zone.y * offsetHeight
      }}
      bounds="parent"
      onDragStop={(e, d) => {
        onChange({
          ...zone,
          x: d.x / offsetWidth,
          y: d.y / offsetHeight
        });
      }}
      onResizeStop={(e, dir, ref, delta, pos) => {
        onChange({
          ...zone,
          w: ref.offsetWidth / offsetWidth,
          h: ref.offsetHeight / offsetHeight,
          x: pos.x / offsetWidth,
          y: pos.y / offsetHeight
        });
      }}
      style={{ zIndex: zone.zIndex }}
      className="border-2 border-indigo-500 bg-indigo-100/30"
    >
      {/* Zone name */}
      <div className="text-xs p-1 font-semibold">
        {zone.name}
      </div>
      <label className="flex items-center gap-1 text-xs mt-1">
        <input
          type="checkbox"
          checked={zone.locked}
          onChange={e =>
            updateZone(zone._id, { locked: e.target.checked })
          }
        />
        ðŸ”’ Lock
      </label>
      {/* Playlist selector */}
      <select
        className="w-full text-xs bg-black text-white rounded px-1 py-1"
        value={zone.playlistId || ""}
        onChange={e =>
          updateZone(zone._id, {
            playlistId: e.target.value || null
          })
        }
      >
        <option value="">No playlist</option>
        {playlists.map(p => (
          <option key={p._id} value={p._id}>
            {p.name}
          </option>
        ))}
      </select>
    </Rnd>
  );
}

export default Zone;
