import { useState } from "react";
import PlaylistBuilder from "./PlaylistBuilder";

export default function ScreenTree({ screens = [] }) {
  const [openPlaylist, setOpenPlaylist] = useState(null);
  const [loadedScreens, setScreens] = useState(screens);

  if (!Array.isArray(screens)) {
    return <p className="text-red-500">Invalid screens data</p>;
  }

  useEffect(() => {
  api.get("/screens")
    .then(res => {
      console.log("SCREENS API:", res.data);
      setScreens(res.data || []);
    })
    .catch(err => {
      console.error("Failed to load screens", err);
      setScreens([]);
    });
}, []);

  return (
    <div className="space-y-2">
      {screens.map(screen => (
        <div key={screen._id} className="border p-3 rounded">
          <p className="font-semibold">{screen.name}</p>

          {screen.playlistId ? (
            <>
              <button
                className="text-blue-600 text-sm"
                onClick={() => setOpenPlaylist(screen.playlistId)}
              >
                Open Playlist
              </button>

              {openPlaylist === screen.playlistId && (
                <PlaylistBuilder playlistId={screen.playlistId} />
              )}
            </>
          ) : (
            <p className="text-sm text-gray-500">No playlist assigned</p>
          )}
        </div>
      ))}
    </div>
  );
}
