import { useEffect, useState } from "react";

function App() {
  // ---------- STATE ----------
  const [name, setName] = useState("");
  const [playlists, setPlaylists] = useState([]);
  const [screens, setScreens] = useState([]);

  const [selectedScreen, setSelectedScreen] = useState("");
  const [selectedPlaylist, setSelectedPlaylist] = useState("");

  // ------API-CALL=======
  const API = "http://localhost:8080";

  // ---------- CREATE PLAYLIST ----------
  async function createPlaylist() {
    if (!name) {
      alert("Enter playlist name");
      return;
    }

    await fetch("http://localhost:8080/playlists", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name })
    });

    setName("");
    loadData();
  }

  // ---------- LOAD PLAYLISTS & SCREENS ----------
async function loadData() {
  const playlistsRes = await fetch(`${API}/playlists`);
  const playlistsText = await playlistsRes.text();

  console.log("Playlists response:", playlistsText);

  const screensRes = await fetch(`${API}/screens`);
  const screensText = await screensRes.text();

  console.log("Screens response:", screensText);

  setPlaylists(JSON.parse(playlistsText));
  setScreens(JSON.parse(screensText));
}

  // ---------- ASSIGN PLAYLIST ----------
  async function assignPlaylist() {
  await fetch("/assign", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      screenId: selectedScreen,
      playlistId: selectedPlaylist
    })
  });

  alert("Playlist assigned");
}


  // ---------- INITIAL LOAD ----------
  useEffect(() => {
    loadData();
  }, []);

  // ---------- UI ----------
  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-3xl mx-auto bg-white rounded shadow p-6">
         <h1 className="text-2xl font-bold mb-6">
          Digital Signage Admin
        </h1>
        <div className="grid grid-cols-2 gap-4 mb-6">
        <select
            className="border p-2 rounded"
            value={selectedScreen}
            onChange={(e) => setSelectedScreen(e.target.value)}
          >
            <option value="">Select Screen</option>
            {screens.map((s) => (
              <option key={s.id} value={s.id}>
                {s.name}
              </option>
            ))}
          </select>
           <select
            className="border p-2 rounded"
            value={selectedPlaylist}
            onChange={(e) => setSelectedPlaylist(e.target.value)}
          >
            <option value="">Select Playlist</option>
            {playlists.map((p) => (
              <option key={p.id} value={p.id}>
                {p.name}
              </option>
            ))}
          </select>

      </div>
       <button
          onClick={assignPlaylist}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
        >
          Assign Playlist
        </button>
      </div>

      {/* Create Playlist */}
      <h3>Create Playlist</h3>
      <input
        value={name}
        onChange={e => setName(e.target.value)}
        placeholder="Playlist name"
      />
      <button onClick={createPlaylist}>Create</button>

    </div>
  );
}

export default App;
