import { useEffect, useState } from "react";
import api from "../api/services";
import { Monitor } from "lucide-react";
import { List } from 'lucide-react';
import { MonitorPlay } from 'lucide-react';
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

export default function ScreenTree() {
  const [screens, setScreens] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get("/screens").then(res => setScreens(res.data));
  }, []);

  return (
    <div className="space-y-1 px-2">
      {screens.map(screen => (
        <button
          key={screen._id}
          onClick={() => navigate(`/player/${screen._id}`)}
          className="w-full flex items-center gap-2 px-3 py-2 rounded hover:bg-gray-800"
        >
          <Monitor size={16} />
          <span className="text-sm">{screen.name}</span>
        </button>
      ))}
      <ul className="mt-2 border-t pt-2 text-sm text-white-500 space-y-1 font-medium">
        <li className="mb-4 flex gap-2"><MonitorPlay/><Link to="/screens">Create Screens</Link></li>
        <li className="mb-4 flex gap-2"><List/><Link to="/playlists">Create Playlists</Link></li>
      </ul>
    </div>
  );
}
