import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/services";
import socket from "../utils/socket";
import usePlaylistPlayer from "../hooks/usePlaylistPlayer";
import ScreenControls from "../components/ScreenControls";

export default function ScreenPreview() {
  const { screenId } = useParams();

  const [mode, setMode] = useState("horizontal");
  const [items, setItems] = useState([]);
  const [playlistId, setPlaylistId] = useState(null);

  /* üîÅ auto-rotate logic */
  const { current, remaining } = usePlaylistPlayer(items);

  /* LOAD SCREEN ‚Üí GET PLAYLIST */
  useEffect(() => {
    async function loadScreen() {
      const res = await api.get(`/screens/${screenId}`);
      setPlaylistId(res.data.playlistId);
    }
    loadScreen();
  }, [screenId]);

  /* LOAD PLAYLIST ITEMS + SOCKET */
  useEffect(() => {
    if (!playlistId) return;

    loadPlaylist();

    socket.emit("register-playlist", playlistId);

    socket.on("playlist-updated", ({ playlistId: id }) => {
      if (id === playlistId) loadPlaylist();
    });

    return () => {
      socket.off("playlist-updated");
    };
  }, [playlistId]);

  async function loadPlaylist() {
    const res = await api.get(`/playlists/${playlistId}/items`);
    setItems(res.data || []);
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">

      {/* HEADER */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-semibold">
          Screen Preview ‚Äî {screenId}
        </h2>

        {/* MODE TABS */}
        <div className="flex bg-gray-800 rounded-lg overflow-hidden">
          <button
            onClick={() => setMode("horizontal")}
            className={`px-4 py-2 text-sm ${
              mode === "horizontal"
                ? "bg-indigo-600"
                : "hover:bg-gray-700"
            }`}
          >
            Horizontal
          </button>
          <button
            onClick={() => setMode("vertical")}
            className={`px-4 py-2 text-sm ${
              mode === "vertical"
                ? "bg-indigo-600"
                : "hover:bg-gray-700"
            }`}
          >
            Vertical
          </button>
        </div>
      </div>

      {/* PREVIEW AREA */}
      <div className="flex justify-center mt-6">
        <div
          className={`relative bg-black rounded-xl shadow-lg overflow-hidden ${
            mode === "horizontal"
              ? "w-[900px] h-[500px]"
              : "w-[400px] h-[700px]"
          }`}
        >
          {/* MEDIA */}
          {current?.mediaId?.type === "image" && (
            <img
              src={current.mediaId.url}
              className="w-full h-full object-cover"
              alt=""
            />
          )}

          {current?.mediaId?.type === "video" && (
            <video
              src={current.mediaId.url}
              autoPlay
              muted
              className="w-full h-full object-cover"
            />
          )}

          {/* COUNTDOWN */}
          {current && (
            <div className="absolute top-3 right-3 bg-black/70 text-white text-xs px-3 py-1 rounded-full">
              ‚è± {remaining}s
            </div>
          )}
        </div>
      </div>
      {/* SCREEN CONTROLS */}
      <ScreenControls screenId={screenId} />
    </div>
  );
}
