import { useEffect, useState } from "react";
import api from "../api/services";
import MediaItem from "./MediaItem";

export default function Sidebar({SelectedScreen}) {
  const [screens, setScreens] = useState([]);
  const [openScreen, setOpenScreen] = useState(null);

  useEffect(() => {
    if (!SelectedScreen) return;

    socket.emit("register-screen", SelectedScreen);

    socket.on("playlist-updated", () => {
      console.log("ðŸ”„ Sidebar refresh");
      loadScreens();
    });

    return () => {
      socket.off("playlist-updated");
    };
  }, [SelectedScreen]);

  async function loadScreens() {
    const res = await api.get("/screens");
    setScreens(res.data || []);
  }

  return (
    <aside className="w-72 bg-slate-900 text-white h-screen overflow-y-auto">
      <h2 className="p-4 font-semibold text-lg border-b border-slate-700">
        Screens
      </h2>

      {screens.map(screen => (
        <div key={screen._id} className="border-b border-slate-800">

          {/* SCREEN HEADER */}
          <button
            onClick={() =>
              setOpenScreen(openScreen === screen._id ? null : screen._id)
            }
            className="w-full text-left px-4 py-3 hover:bg-slate-800"
          >
            <p className="font-medium">{screen.name}</p>
            <p className="text-xs text-slate-400">
              {screen.playlist?.name || "No playlist"}
            </p>
          </button>

          {/* PLAYLIST ITEMS */}
          {openScreen === screen._id && (
            <div className="bg-slate-800 px-3 py-2 space-y-2">
              {!screen.playlist?.items?.length && (
                <p className="text-xs text-slate-400">No media</p>
              )}

              {screen.playlist?.items?.map(item => (
                <MediaItem key={item._id} item={item} />
              ))}
            </div>
          )}
        </div>
      ))}
    </aside>
  );
}
