import { useEffect, useState } from "react";
import { ChevronDown, ChevronRight, Monitor, Film, Image } from "lucide-react";
import axios from "../utils/api";
import { io } from "socket.io-client";

const socket = io("http://localhost:8080");

export default function ScreenTree() {
  const [screens, setScreens] = useState([]);
  const [openScreens, setOpenScreens] = useState({});

  const loadScreens = async () => {
    const res = await axios.get("/screens/tree");
    setScreens(res.data);
  };

  useEffect(() => {
    loadScreens();

    socket.on("playlist-updated", loadScreens);

    return () => socket.off("playlist-updated");
  }, []);

  const toggle = (id) => {
    setOpenScreens((prev) => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  return (
    <div className="bg-gray-900 text-white rounded-xl p-4">
      <h3 className="font-semibold mb-3 text-gray-300">Screens</h3>

      {screens.map((screen) => (
        <div key={screen._id} className="mb-2">
          {/* SCREEN */}
          <button
            onClick={() => toggle(screen._id)}
            className="flex items-center gap-2 w-full text-left hover:bg-gray-800 px-2 py-1 rounded"
          >
            {openScreens[screen._id] ? (
              <ChevronDown size={16} />
            ) : (
              <ChevronRight size={16} />
            )}
            <Monitor size={16} />
            <span>{screen.name}</span>
          </button>

          {/* PLAYLIST */}
          {openScreens[screen._id] && screen.playlist && (
            <div className="ml-6 mt-2 space-y-1">
              <div className="text-sm text-blue-400">
                ðŸ“‚ {screen.playlist.name}
              </div>

              {/* MEDIA ITEMS */}
              {screen.playlist.items.map((item) => (
                <div
                  key={item._id}
                  className="ml-4 flex items-center gap-2 text-sm text-gray-300"
                >
                  {item.media.type === "image" ? (
                    <Image size={14} />
                  ) : (
                    <Film size={14} />
                  )}
                  <span className="truncate max-w-[140px]">
                    {item.media.url.split("/").pop()}
                  </span>
                  <span className="text-gray-500">
                    {item.duration}s
                  </span>
                </div>
              ))}
            </div>
          )}

          {openScreens[screen._id] && !screen.playlist && (
            <div className="ml-8 text-sm text-gray-500">
              No playlist assigned
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
