import { useState, useEffect } from "react";
import PlaylistBuilder from "./PlaylistBuilder";
import api from "../api/services";

export default function ScreenTree() {
  const [screens, setScreens] = useState([]);
  const [openPlaylist, setOpenPlaylist] = useState(null);

  useEffect(() => {
    api.get("/screens")
      .then(res => {
        console.log("SCREENS API:", res.data);
        setScreens(res.data || []);
      })
      .catch(err => {
        console.error("Failed to load screens", err);
        setScreens([]);
      });
  }, []);

  if (!screens.length) {
    return <p className="text-gray-500">No screens found</p>;
  }

  return (
    <div className="space-y-2">
      {screens.map(screen => (
        <div key={screen._id} className="border p-3 rounded">
          <p className="font-semibold">{screen.name}</p>

          {screen.playlistId ? (
            <>
              <button
                className="text-blue-600 text-sm"
                onClick={() =>
                  setOpenPlaylist(
                    openPlaylist === screen.playlistId
                      ? null
                      : screen.playlistId
                  )
                }
              >
                {openPlaylist === screen.playlistId
                  ? "Close Playlist"
                  : "Open Playlist"}
              </button>

              {openPlaylist === screen.playlistId && (
                <div className="mt-2">
                  <PlaylistBuilder playlistId={screen.playlistId} />
                </div>
              )}
            </>
          ) : (
            <p className="text-sm text-gray-500">No playlist assigned</p>
          )}
        </div>
      ))}
    </div>
  );
}
