import Zone from "./Zone";

function ZoneEditor({ zones, setZones, containerRef, playlists }) {
  if (!containerRef?.current) return null;

    function updateZone(zoneId, patch) {
    setZones(prev =>
      prev.map(z =>
        z._id === zoneId ? { ...z, ...patch } : z
      )
    );
  }

  return (
      <>
      {zones.map(zone => (
        <Zone
          key={zone._id}
          zone={zone}
          playlists={playlists}
          updateZone={updateZone}
        />
      ))}
    </>
  );
}

export default ZoneEditor;
