import usePlaylistPlayer from "../../hooks/usePlaylistPlayer";
import MediaPlayer from "../MediaPlayer";
import LiveWidgetRenderer from "../../components/LiveWidgetRenderer";

export default function ZoneContentResolver({
  zone,
  playlistItems = [],
  groupPlaylistItems = [],
  fallbackMedia
}) {
  /* ---------- PRIORITY ---------- */

  // Live widget
  if (zone?.widget) {
    return <LiveWidgetRenderer widget={zone.widget} />;
  }

  // Zone playlist
  const items =
    playlistItems.length
      ? playlistItems
      : groupPlaylistItems.length
      ? groupPlaylistItems
      : fallbackMedia
      ? [{ mediaId: fallbackMedia }]
      : [];

  const { current } = usePlaylistPlayer(items);

  if (current?.mediaId) {
    return <MediaPlayer media={current.mediaId} />;
  }

  // Empty
  return (
    <div className="w-full h-full flex items-center justify-center text-xs text-gray-400">
      No content
    </div>
  );
}
