import { PlayCircle, MapPin } from "lucide-react";

export default function PlayerCard({ screen }) {
  return (
    <div className="group relative flex items-center gap-4 rounded-xl border bg-white p-4 shadow-sm transition-all duration-300 hover:-translate-y-1 hover:shadow-lg cursor-pointer">

      {/* Icon */}
      <div className="flex h-14 w-14 items-center justify-center rounded-full bg-gradient-to-br from-indigo-500 to-indigo-600 text-white shadow-md group-hover:scale-105 transition">
        <PlayCircle size={28} strokeWidth={2} />
      </div>

      {/* Info */}
      <div className="flex-1">
        <div className="flex items-center gap-2">
          <h3 className="font-semibold text-gray-900 truncate">
            {screen.name}
          </h3>

          {/* Status dot */}
          <span className="h-2 w-2 rounded-full bg-green-500 animate-pulse" />
        </div>

        <div className="mt-1 flex items-center gap-1 text-xs text-gray-500">
          <MapPin size={12} />
          <span>{screen.location || "No location assigned"}</span>
        </div>
      </div>

      {/* Action */}
      <div className="text-xs text-indigo-600 font-medium opacity-0 group-hover:opacity-100 transition">
        View
      </div>
    </div>
  );
}
