import {resolveBindings} from "../utils/resolveBindings";

export default function MediaPlayer({ media }) {
  const dataContext = {
  time: new Date().toLocaleTimeString(),
  weather: {
    temp: 32
  }
};

const finalHtml = resolveBindings(media.content, dataContext);

  if (!media) return null;

  // IMAGE
  if (media.type === "image") {
    return (
      <img
        src={media.url}
        className="w-full h-full object-cover"
        alt=""
      />
    );
  }

  // VIDEO
  if (media.type === "video") {
    return (
      <video
        src={media.url}
        autoPlay
        muted
        className="w-full h-full object-cover"
      />
    );
  }

  // TEXT
  if (media.type === "text") {
    return (
      <div
        className="w-full h-full flex items-center justify-center"
        style={{
          fontSize: media.meta?.fontSize || 48,
          color: media.meta?.color || "#fff",
          background: media.meta?.bgColor || "#000"
        }}
      >
        {media.content}
      </div>
    );
  }

  // HTML
  if (media.type === "html") {
    return (
      <div
        className="w-full h-full"
        dangerouslySetInnerHTML={{ __html: finalHtml }}
      />
    );
  }

  return null;
}
