import { useEffect, useState } from "react";

const API = "http://localhost:8080";

function App() {
  // ---------- STATE ----------
  const [name, setName] = useState("");
  const [playlists, setPlaylists] = useState([]);
  const [screens, setScreens] = useState([]);

  const [selectedScreen, setSelectedScreen] = useState("");
  const [selectedPlaylist, setSelectedPlaylist] = useState("");

  // ---------- LOAD DATA ----------
  async function loadData() {
    const playlistsRes = await fetch(`${API}/playlists`);
    const screensRes = await fetch(`${API}/screens`);

    setPlaylists(await playlistsRes.json());
    setScreens(await screensRes.json());
  }

  // ---------- CREATE PLAYLIST ----------
  async function createPlaylist() {
    if (!name) {
      alert("Enter playlist name");
      return;
    }

    await fetch(`${API}/playlists`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name }),
    });

    setName("");
    loadData();
  }

  // ---------- ASSIGN PLAYLIST ----------
  async function assignPlaylist() {
    if (!selectedScreen || !selectedPlaylist) {
      alert("Select screen and playlist");
      return;
    }

    await fetch(`${API}/assign`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        screenId: Number(selectedScreen),
        playlistId: Number(selectedPlaylist),
      }),
    });

    alert("Playlist assigned");
  }

  // ---------- INITIAL LOAD ----------
  useEffect(() => {
    loadData();
  }, []);

  // ---------- UI ----------
  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow p-6 space-y-6">

        <h1 className="text-2xl font-bold">
          Digital Signage Admin
        </h1>

        {/* Assign Playlist */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <select
            className="border p-2 rounded"
            value={selectedScreen}
            onChange={(e) => setSelectedScreen(e.target.value)}
          >
            <option value="">Select Screen</option>
            {screens.map((s) => (
              <option key={s.id} value={s.id}>
                {s.name}
              </option>
            ))}
          </select>

          <select
            className="border p-2 rounded"
            value={selectedPlaylist}
            onChange={(e) => setSelectedPlaylist(e.target.value)}
          >
            <option value="">Select Playlist</option>
            {playlists.map((p) => (
              <option key={p.id} value={p.id}>
                {p.name}
              </option>
            ))}
          </select>

          <button
            onClick={assignPlaylist}
            className="bg-blue-600 hover:bg-blue-700 text-white rounded px-4 py-2"
          >
            Assign Playlist
          </button>
        </div>

        {/* Create Playlist */}
        <div className="border-t pt-4">
          <h3 className="font-semibold mb-2">Create Playlist</h3>

          <div className="flex gap-2">
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Playlist name"
              className="border p-2 rounded flex-1"
            />
            <button
              onClick={createPlaylist}
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
            >
              Create
            </button>
          </div>
        </div>

      </div>
    </div>
  );
}

export default App;
