import { useEffect, useState } from "react";
import api from "../api/services";
import socket  from "../utils/socket";

export default function PlaylistBuilder({ playlistId }) {
  const [media, setMedia] = useState([]);
  const [items, setItems] = useState([]);
  const [mediaId, setMediaId] = useState("");
  const [duration, setDuration] = useState(10);
  const [file, setFile] = useState(null);
  const [dragIndex, setDragIndex] = useState(null);

  async function load() {
    const res = await api.get(`/playlists/${playlistId}/items`);
    setItems(res.data);
  }

   /* LOAD MEDIA LIBRARY */
  useEffect(() => {
    api.get("/media").then((res) => setMedia(res.data));
  }, []);

  useEffect(() => {
    if (!playlistId) return;
    socket.emit("register-playlist", playlistId);
    load();

    socket.on("playlist-updated", ({ playlistId: id }) => {
      if (id === playlistId) load();
    });

    return () => socket.off("playlist-updated");
  }, [playlistId]);

  function moveItem(target) {
    const updated = [...items];
    const [moved] = updated.splice(dragIndex, 1);
    updated.splice(target, 0, moved);
    setItems(updated);

    api.post("/playlists/reorder", {
      playlistId,
      items: updated.map((i, idx) => ({
        id: i._id,
        position: idx
      }))
    });
  }

  /* UPLOAD MEDIA */
  async function uploadMedia() {
    if (!file) return alert("Select a file");

    try {
      const form = new FormData();
      form.append("file", file); // ðŸ”´ MUST be "file"

      await api.post("/media/upload", form);

      setFile(null);

      const res = await api.get("/media");
      setMedia(res.data);
    } catch (err) {
      console.error("UPLOAD ERROR:", err.response?.data || err.message);
      alert(err.response?.data?.error || "Upload failed");
    }
  }

    async function addToPlaylist() {
    if (!mediaId) return alert("Select media");

    await api.post("/playlists/items", {
      playlistId,
      mediaId,
      duration: Number(duration),
      position: Date.now(),
    });

    setMediaId("");
    loadItems();
  }

  return (
    <div className="space-y-3 bg-gray-50 p-3 rounded overflow-visible">
      <div className="font-semibold">Playlist Items</div>
       <div className="flex flex-col items-start gap-2">
          <label
            htmlFor="fileUpload"
            className="w-full flex flex-col items-center justify-center border-2 border-dashed border-gray-300 rounded-lg p-6 cursor-pointer hover:border-indigo-500 hover:bg-indigo-50 transition-colors"
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-upload-icon lucide-upload"><path d="M12 3v12"/><path d="m17 8-5-5-5 5"/><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/></svg>
            <span className="text-gray-600 text-sm text-center">
              Drag & drop your file here, or click to select
            </span>
            <input
              id="fileUpload"
              type="file"
              accept="image/*,video/*"
              onChange={(e) => setFile(e.target.files[0])}
              className="hidden"
            />
          </label>

          {/* Upload Button */}
          <button
            onClick={uploadMedia}
            className="bg-indigo-600 text-white text-sm px-4 py-2 rounded hover:bg-indigo-700 transition"
            disabled={!file}
          >
            Upload
          </button>
       </div>
       <div className="flex gap-2">
        <select
          className="border p-2 rounded"
          value={mediaId}
          onChange={(e) => setMediaId(e.target.value)}
        >
          <option value="">Select Media</option>
          {media.map((m) => (
            <option key={m._id} value={m._id}>
              {m.filename} ({m.type})
            </option>
          ))}
        </select>

        <input
          type="number"
          className="border p-2 w-20"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
        />

        <button
          onClick={addToPlaylist}
          className="bg-indigo-600 text-white px-3 rounded"
        >
          Add
        </button>
      </div>
      <div>
      {items.map((i, index) => (
        <div
          key={i._id}
          draggable
          onDragStart={() => setDragIndex(index)}
          onDragOver={e => e.preventDefault()}
          onDrop={() => moveItem(index)}
          className="border p-2 mb-1 cursor-move"
        >
          {!i.mediaId ? (
            <span className="text-red-500 text-xs">
              Media missing
            </span>
          ) : (
            <>
              <span>{i.mediaId.filename}</span>
              <span className="text-gray-500">{i.duration}s</span>
            </>
          )}
        </div>
      ))}
      </div>
    </div>
  );
}
