import { useEffect, useState } from "react";
import api from "../api/services";
import usePlaylistPlayer from "../hooks/usePlaylistPlayer";
import MediaPlayer from "./MediaPlayer";
import LiveWidgetRenderer from "./LiveWidgetRenderer";

export default function ZonePlayer({
  zone,
  activeTimelinePlaylistId // ⬅️ pass playlistId, not object
}) {
  const [playlistItems, setPlaylistItems] = useState([]);
  const [fallback, setFallback] = useState(null);

  /* ---------- PLAYLIST RESOLUTION ---------- */
  const playlistId =
    activeTimelinePlaylistId ??
    zone?.playlistId ??
    null;

  /* ---------- LOAD PLAYLIST ITEMS ---------- */
  useEffect(() => {
    if (!playlistId || zone?.widget) return;

    api
      .get(`/playlists/${playlistId}/active-items`)
      .then(res => setPlaylistItems(res.data || []))
      .catch(() => setPlaylistItems([]));
  }, [playlistId, zone?.widget]);

  /* ---------- LOAD FALLBACK MEDIA ---------- */
  useEffect(() => {
  const id = zone?.fallbackMediaId;
  if (!id) return;

  api.get(`/media/${id}`)
    .then(res => setFallback(res.data))
    .catch(err => {
      console.error("Fallback load failed", err.response?.status);
      setFallback(null);
    });
}, [zone?.fallbackMediaId]);

  /* ---------- BUILD PLAY QUEUE ---------- */
  const itemsToPlay =
    playlistItems.length
      ? playlistItems
      : fallback
      ? [{ mediaId: fallback }]
      : [];

  const { current } = usePlaylistPlayer(itemsToPlay);

  /* ---------- RENDER PRIORITY ---------- */

  // 1️⃣ Widget overrides everything
  if (zone?.widget) {
    return <LiveWidgetRenderer widget={zone.widget} />;
  }

  // 2️⃣ Playlist / fallback media
  if (current?.mediaId) {
    return <MediaPlayer media={current.mediaId} />;
  }

  // 3️⃣ Empty state (IMPORTANT)
  return (
    <div className="w-full h-full flex items-center justify-center text-xs text-gray-400">
      No content assigned
    </div>
  );
}
