import { useEffect, useState } from "react";
import api from "../api/services";
import { Link, Monitor } from "lucide-react";
import { useNavigate } from "react-router-dom";

export default function ScreenTree() {
  const [screens, setScreens] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get("/screens").then(res => setScreens(res.data));
  }, []);

  return (
    <div className="space-y-1 px-2">
      {screens.map(screen => (
        <button
          key={screen._id}
          onClick={() => navigate(`/player/${screen._id}`)}
          className="w-full flex items-center gap-2 px-3 py-2 rounded hover:bg-gray-800"
        >
          <Monitor size={16} />
          <span className="text-sm">{screen.name}</span>
        </button>
      ))}
      <ul className="mt-2 border-t pt-2 text-xs text-gray-500">
        <li className="mb-2"><Link onClick={()=> navigate('/screens')}></Link>Create Screens</li>
        <li className="mb-2"><Link onClick={()=> navigate('/playlists')}>Create Playlists</Link></li>
      </ul>
    </div>
  );
}
