import { useEffect, useRef, useState } from "react";
import api from "../api/services";
import usePlaylistPlayer from "../hooks/usePlaylistPlayer";
import MediaPlayer from "./MediaPlayer";

export default function ZonePlayer({
  screenId,
  zoneId,
  playlistId,
  fallbackMediaId
}) {
  const [items, setItems] = useState([]);
  const [fallbackMedia, setFallbackMedia] = useState(null);
  const loggedRef = useRef(null);

  /* ðŸŽž LOAD PLAYLIST ITEMS */
  useEffect(() => {
    if (!playlistId) {
      setItems([]);
      return;
    }

    api
      .get(`/playlists/${playlistId}/active-items`)
      .then(res => setItems(res.data || []))
      .catch(() => setItems([]));
  }, [playlistId]);

  /* ðŸ›Ÿ LOAD FALLBACK MEDIA */
  useEffect(() => {
    if (!fallbackMediaId) {
      setFallbackMedia(null);
      return;
    }

    api
      .get(`/media/${fallbackMediaId}`)
      .then(res => setFallbackMedia(res.data))
      .catch(() => setFallbackMedia(null));
  }, [fallbackMediaId]);

  /* ðŸŽ¯ PLAYLIST PLAYER */
  const itemsToPlay =
    items.length > 0
      ? items
      : fallbackMedia
        ? [{ mediaId: fallbackMedia, duration: fallbackMedia.duration || 10 }]
        : [];

  const { current } = usePlaylistPlayer(itemsToPlay);

  /* ðŸ“Š ZONE-LEVEL ANALYTICS */
  useEffect(() => {
    if (!current?.mediaId?._id) return;

    if (loggedRef.current === current.mediaId._id) return;
    loggedRef.current = current.mediaId._id;

    api.post("/analytics/log", {
      screenId,
      mediaId: current.mediaId._id,
      zoneId
    }).catch(() => {});
  }, [current, screenId, zoneId]);

  if (!current?.mediaId) return null;

  return <MediaPlayer media={current.mediaId} />;
}
