import { useState } from "react";
import PlaylistBuilder from "./PlaylistBuilder";

export default function ScreenTree({ screens }) {
  const [openPlaylist, setOpenPlaylist] = useState(null);

  return (
    <div>
      {screens.map(screen => (
        <div key={screen._id} className="border p-2">
          <p className="font-semibold">{screen.name}</p>

          {screen.playlist && (
            <button
              onClick={() => setOpenPlaylist(screen.playlist._id)}
              className="text-blue-600 text-sm"
            >
              Open Playlist
            </button>
          )}

          {openPlaylist === screen.playlist?._id && (
            <PlaylistBuilder playlistId={screen.playlist._id} />
          )}
        </div>
      ))}
    </div>
  );
}
